        <Blog
          id={blog._id}
          isUser={localStorage.getItem("userId") === blog.user._id}
          title={blog.title}
          content={blog.content}
          image={blog.image}
          userName={blog.userIp}
        />
      ))}
        {localStorage.setItem("Blogs Details", JSON.stringify(blogs))}






      </>
    ) : (
      <>

       <h1>Not found</h1>
      </>
    )}




  </div>












  );
}

export default Blogs;
