import React from "react";
import Blog from "./Blog";


const BlogSearch=({arr})=>{

    return (
        <>
{
arr.map((blog, index) => (
          <Blog
            id={blog._id}
            isUser={localStorage.getItem("userId") === blog.user._id}
            title={blog.title}
            content={blog.content}
            image={blog.image}
            userName={blog.userIp}
          />
        ))}
        
        
        
        </>
    );
}

export default BlogSearch;
